# Get price ratio
accesspoint=''
# accesspoint will look something like this - 'https://mainnet.infura.io/v3/5c061fb3f43f47319c03266dc2ffe647' . stick your infura key here

# create w3 instance
w3 = Web3(Web3.HTTPProvider(accesspoint))

eth_usdt_pair_address_lowercase = "0x0d4a11d5eeaac28ec3f61d100daf4d40471f1852"
pair_address_checksum = Web3.toChecksumAddress(eth_usdt_pair_address_lowercase)
#instantiate the contract class
pair = w3.eth.contract(address=pair_address_checksum, abi=create_uniswap_abi())
# get latest block number
end_block = w3.eth.getBlock('latest')['number']
reserve_info=pair.functions.getReserves().call()

print(reserve_info,type(reserve_info))

t0_address = pair.functions.token0().call()
t1_address = pair.functions.token1().call()

t0_contract = w3.eth.contract(address=Web3.toChecksumAddress(t0_address), abi=create_erc20_abi())
t1_contract = w3.eth.contract(address=Web3.toChecksumAddress(t1_address), abi=create_erc20_abi())

d0 = t0_contract.functions.decimals().call() # decimals
d1 = t1_contract.functions.decimals().call()

t0_name = t0_contract.functions.name().call()
t1_name = t1_contract.functions.name().call()

t0_symbol = t0_contract.functions.symbol().call()
t1_symbol = t1_contract.functions.symbol().call()

reserve0_adj = reserve_info[0]/10**d0
reserve1_adj = reserve_info[1]/10**d1

print("{} ({}) Reserve(adjusted with decimal) :".format(t0_name,t0_symbol,reserve0_adj))
print("{} ({}) Reserve(adjusted with decimal) :".format(t1_name,t1_symbol,reserve1_adj))
print("token 0 / token 1 :",reserve0_adj/reserve1_adj)
print("token 1 / token 0 :",reserve1_adj/reserve0_adj)
print("######################################################")
